package Lec1;

public class Assignment_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		x += 3;// x = x+ 3;
		System.out.println(x);
		int p = 9;
		p -= 5;// p = p-s;
		System.out.println(p);
		int a = 9;
		a *= 5;// a= a*5;
		System.out.println(a);
	}

}
