package Lec1;

public class Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 79;
		if (n >= 50) {
			System.out.println("hey");
		}
		if (n > 100) {
			System.out.println("bye");
		}
		if (n % 2 == 0) {
			System.out.println("Even");
		} else {
			System.out.println("Odd");
		}
	}

}
