package Lec6;

import java.util.Scanner;

public class Typcasting_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float f = (8.9f);
		System.out.println(f);
		double d = 8.9;
		Scanner sc = new Scanner(System.in);
		f = sc.nextFloat();
		d = sc.nextDouble();

	}

}
